        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{ 'Reset your password'|trans }}{% endblock %}

{% block body %}
    <div class=\"mt-5 container col-3 \">
        <h1>{{ 'Reset your password'|trans }}</h1>
        {{ form_start(requestForm, {'attr': {'class': 'form-horizontal'}}) }}
        {{ form_row(requestForm.email, {'attr':
            {'class': 'form-control', 'required': 'true',
            'placeholder': 'Email'}
        }) }}
        <div>
            <small>
                {{'Enter your email address and we we will send you a
                link to reset your password.' }}
            </small>
        </div>
        <br>
        <button class=\"btn btn-primary\">{{ 'Send password reset email'|trans }}</button>
        {{ form_end(requestForm) }}
    </div>
{% endblock %}", "reset_password/request.html.twig", "/var/www/php/symfony/templates/reset_password/request.html.twig");
